var userOBJ = function(arr){
	"use strict";
	this.mode = arr.mode;
	this.lastquiz = Number(arr.lastquiz);
	this.part = Number(arr.part);
	this.choices = arr.choices;
	this.Mchoices = arr.Mchoices;
	this.setting1 = Number(arr.setting1);
	this.setting2 = Number(arr.setting2);
	this.ReadingTxt = Number(arr.ReadingTxt);
	this.timer = Number(arr.timer);
	this.lstlgin = Number(arr.lstlgin);
	this.set = function(key, value,array){
		if(array !== undefined){
			array = checkArray(array);
		}
		var db = new DB_USER();
		db.updateUserData_Individual(key,value,array);
	};

	this.initAll = function(){
		var arr = [["part",1],["lastquiz",0],["choices",""]];
		userDataArray.set('','',arr);
		ansResult = [];
	};

	this.init = function(key){
		switch (key){
			case "part":
			userDataArray.set(key,1);
			break;

			case "lastquiz": 
			userDataArray.set(key,0);
			break;

			case "choices": 
			userDataArray.set(key,"");
			break;

			case "ans": case "ansResult":
			ansResult = [];
			break;
		}
	};

	function checkArray(array){
		for(var i = 0;i<array.length;i++){
			if(array[i][1] ===undefined){
				if(array[i][0] === "user"){
					array[i][1] = 0;
				}
				if(array[i][0] === "mode"){
					array[i][1] ="Part";
				}
				if(array[i][0] === "part"){
					array[i][1] = 1;
				}
				if(array[i][0] === "choces"){
					array[i][1] = "";
				}
				if(array[i][0] === "setting1"){
					array[i][1] = 0;
				}
				if(array[i][0] === "setting2"){
					array[i][1] = 0;
				}
				if(array[i][0] === "readingTxt"){
					array[i][1] = 1;
				}
				if(array[i][0] === "timer"){
					array[i][1] =75;
				}
			} 
		}
		return array;
	}
};

