var scoreOBJ = function(arr){
	"use strict";
	for(var i=0;i<arr.length;i++){
		var scoreArr = setPercentage(arr[i].ans);
		arr[i]={id:arr[i].id, mode:arr[i].mode,part:arr[i].part,ans:arr[i].ans, score:scoreArr};
	}
	this.arr = arr.reverse();
	if(arr){
		var array2 = [];
		for(var t=0;t<arr.length;t++){
			array2[arr[t].id] = {mode:arr[t].mode,part:arr[t].part,ans:arr[t].ans, score:arr[t].score};
		}
		this.idArr = array2.reverse();
	}
	
	this.set = function(array){
		var db = new DB_Score();
		db.updateData_Score(array);
	};

	this.delete = function(array){
		var db = new DB_Score();
		db.deleteData_Score(array);
	};

	this.makeCalDB = function (txt){
		var year = txt.slice(0,2);
		var month = txt.slice(2,4);
		var day = txt.slice(4,6);
		var hour = txt.slice(6,8);
		var min = txt.slice(8,10);
		var sec;
		if(txt.length < 12){
			sec = txt.slice(10,11);
		}else{
			sec = txt.slice(10,12);
		}
		var returnTxt = year + "年" +　month + "月" +　day + "日" +　hour + "時" +　min + "分";
		//　+　sec + "秒"
		//秒まで必要なら上を足す。
		return returnTxt;
	};
	this.checkHowManyAns = function(txt){
		var arr = txt.split("@");
		var NumOfQuiz = arr.length;
		var correctAns = 0;
		for(var i in arr){
			if(arr.hasOwnProperty(i)){
				var ans = Number(arr[i].slice(-1));
				if(ans === 1){
					correctAns++;
				}
			}
		}
		var score = Math.round(correctAns/NumOfQuiz * 100);
		var returnText = correctAns + '/' + NumOfQuiz ;
		return returnText;		
	};
	function setPercentage(txt){
		var arr = txt.split("@");
		var NumOfQuiz = arr.length;
		var correctAns = 0;
		for(var i in arr){
			if(arr.hasOwnProperty(i)){
				var ans = Number(arr[i].slice(-1));
				if(ans === 1){
					correctAns++;
				}
			}
		}
		var score = Math.round(correctAns/NumOfQuiz * 100);
		var array = {percent:score, txt:"correctAns/NumOfQuiz"};
		return array;
	}
};

var quizObj = function(No,flg){
	"use strict";
	if(!flg){
		currentQNo = Number(quizArray[No].Number);
	}
	this.Number = Number(quizArray[No].Number);
	this.Part = Number(quizArray[No].Part);
	this.Section = Number(quizArray[No].Section);
	switch (Number(quizArray[No].Part)){
		case 1: case 2:
		this.Mondai = quizArray[No].Mondai;
		this.Kaiwa = "";
		this.KYaku = "";
		this.mp3src = 'mp3/' + Number(quizArray[No].Number) + '.mp3';
		break;

		case 5:
		this.Mondai = quizArray[No].Mondai;
		this.Kaiwa = "";
		this.KYaku = "";
		break;

		case 3: case 4: 
		//会話・長文は最初の問題にしか入っていないのでsection（最初の問題の番号）から取り出す
		this.Mondai = quizArray[No].Mondai;
		this.Kaiwa = quizArray[Number(quizArray[No].Section)].Kaiwa;
		this.KYaku = quizArray[Number(quizArray[No].Section)].KYaku;
		this.mp3src = 'mp3/' + quizArray[No].Section + '.mp3';
		break;

		case 6: 
		//会話・長文は最初の問題にしか入っていないのでsection（最初の問題の番号）から取り出す
		this.Mondai = "";
		this.Kaiwa =quizArray[Number(quizArray[No].Section)].Kaiwa;
		this.KYaku = quizArray[Number(quizArray[No].Section)].KYaku;
		break;

		case 7: 
		//会話・長文は最初の問題にしか入っていないのでsection（最初の問題の番号）から取り出す
		this.Mondai = quizArray[No].Mondai;
		this.Kaiwa = quizArray[Number(quizArray[No].Section)].Kaiwa;
		this.KYaku = quizArray[Number(quizArray[No].Section)].KYaku;
		break;
	}

	this.Choice = quizArray[No].Choice;
	this.CYaku = quizArray[No].CYaku;
	this.Answer = quizArray[No].Answer;
	this.Expranation = quizArray[No].Expranation;
	this.StartStop = quizArray[No].StartStop;
	if(Number(quizArray[No].Picture)===1){
		this.picsrc = 'images/' + Number(quizArray[No].Number) + '.jpg';
		this.picsrcJ = 'images/' + Number(quizArray[No].Number) + 'J.jpg';
	}else{
		this.picsrc = null;
	}

	this.makeChoices = function(str){
		//(A)などが邪魔なのでここで削ります。
		var t_arr = str.replace(/\([ABCD]\)/g,"");
		//<br>で調整
		t_arr = t_arr.split('<br>');
		t_arr.A = t_arr[0];
		t_arr.B = t_arr[1];
		t_arr.C = t_arr[2];
		t_arr.D = t_arr[3];
		return t_arr;
	};

};
